import React, {useState, useEffect} from "react";
import {Typography, Grid, CircularProgress, Button} from "@material-ui/core";
import "../styles/Song.css";
import { getPlaylist } from "../services/APIServices";


export default function Song(props) {
    let playListId = "";
    const [loading, setLoading] = useState(true);
    const [song, setSong] = useState();

    useEffect((playListId, sign) => {
        sign = props.userSign;
        if (sign === "Aries") {
            playListId = "37i9dQZF1DX2DC3Q7JOmYe";
        } else if (sign === "Taurus") {
            playListId = "37i9dQZF1DXbCgDGG5xQtb";
        } else if (sign === "Gemini") {
            playListId = "37i9dQZF1DWWVULI5wUsL9";
        } else if (sign === "Cancer") {
            playListId = "37i9dQZF1DXaeX3MJpiD4U";
        } else if (sign === "Leo") {
            playListId = "37i9dQZF1DX7cvHpkIJFt2";
        } else if (sign === "Virgo") {
            playListId = "37i9dQZF1DX6PdsVYbP4rI";
        } else if (sign === "Libra") {
            playListId = "37i9dQZF1DXco4NYQOMLiT";
        } else if (sign === "Scorpio") {
            playListId = "37i9dQZF1DX0YZgrwmizcR";
        } else if (sign === "Sagittarius") {
            playListId = "37i9dQZF1DX93MXPufCcuk";
        } else if (sign === "Capricorn") {
            playListId = "37i9dQZF1DX2rcqmLx0nK4";
        } else if (sign === "Aquarius") {
            playListId = "37i9dQZF1DX7F9VDRJOFhw";
        } else if (sign === "Pisces") {
            playListId = "37i9dQZF1DWX0EDWtabVRv";
        }

        getPlaylist(playListId).then((res)=> {
            console.log(res.data.tracks.items);
            let index = Math.floor(Math.random() * res.data.tracks.items.length)
            console.log(res.data.tracks.items[index].track.name)
            setSong(res.data.tracks.items[index].track);
        }).finally(() => {
            setLoading(false);
        })
    }, [props.userSign]);

    const renderSong = () => {
        if (loading) {
            return (
              <div>
                <Grid
                  container
                  spacing={0}
                  direction="column"
                  alignItems="center"
                  justifyContent="center"
                  style={{ paddingTop: 20 }}
                >
                  <Grid item xs={3}>
                    <CircularProgress />
                  </Grid>
                </Grid>
              </div>
            )}
        else {
            return (
                <div style={{paddingTop: 20}}>
                    <Typography variant="h5">{song.name}</Typography>
                    <Typography variant="subtitle1">By {song.artists.map((artist) => (
                        <span>{artist.name}</span>
                    ))}</Typography>
                    <img src={song.album.images[0].url} alt="album cover"></img>
                </div>
            )
        }
    }


    return (
        <body style={{backgroundColor: "rgba(240, 227, 245, 0.9)"}}>
        <div className="content">
            <Typography variant="h4" style={{paddingTop:20}}>
                Your {props.userSign} song is:
            </Typography>
            {renderSong(playListId, props.userSign)}
            <Button variant="contained" color="primary">I want a different song</Button>
        </div>
        </body>
    );
}
